let scores = ForEach(gamePlays, $item.Score)
let average = Round(Sum(...scores) / Count(scores))
let high = Max(...scores)
let userName = SelectFirst(TheUsers, $item.id === userId).DisplayName
Record('UserId', userId, 'Name', userName, 'Average', average, 'High', high)

let user = GetUserData()
let userId = CurrentUser().uid
If(user, Update(Users, userId, changes), Add(Users, Record(changes, 'id', userId, 'DisplayName', CurrentUser().Name)))

Log('In NewTeam', teamData)
let user = GetUserData()
Check(Not(user?.TeamId), 'Cannot create new team as already a member of a team')
let team = Add(Teams, Record('Name', teamData.TeamName, 'OwnerId', CurrentUser().uid))
UpdateUserData(Record('TeamId', team.id))
Log('Done NewTeam')

let user = GetUserData()
let team = Get(Teams, user?.TeamId)
Check(team?.OwnerId == user.id, 'Cannot invite a team member unless you are the owner')
Add(Invites, Record('id', inviteId, 'TeamId', team.id, 'DateCreated', Now()))

let user = GetUserData()
let invite = Get(Invites, inviteId)
Check(IsNull(invite.DateUsed), 'This invite has already been used')
Check(IsNull(user?.TeamId), 'Cannot join a team if you are already in a team')
UpdateUserData(Record('TeamId', invite.TeamId, 'DateJoinedTeam', Today()))
Update(Invites, inviteId, Record('DateUsed', Now()))

let teamId = GetUserData()?.TeamId
let gamePlays = If(teamId, Query(GamePlays, {TeamId: teamId}))
let users = If(teamId, Query(Users, {TeamId: teamId}))
Record({gamePlays: gamePlays, users: users})
